  PRIMARY KEY (`pid`),
  KEY `npid_ind` (`npid`),
  CONSTRAINT `photo_ibfk_1` FOREIGN KEY (`npid`) REFERENCES `news` (`nid`)
) ENGINE=InnoDB DEFAULT CHARSET=gbk;

